import React from 'react';
import SeasonDisplay from './SeasonDisplay';


class App extends React.Component {
    successCallback = (position) => {
        let msg = `Your latitude is ${position.coords.latitude}`;
        msg += `\r\nAnd your longitude is ${position.coords.longitude}`;
        console.log(msg);
        this.setState({latitude: position.coords.latitude, longitude: position.coords.longitude});
        console.log(this.state);
    }

    errorCallback = (err) => {
        console.log(`Sorry. We cannot detect your location using the browser-based geolocation object`);
        console.log(err);
    }


    constructor(props) {
        this.state = { latitude: '', longitude: ''};
    }

    render() {
        window.navigator.geolocation.getCurrentPosition(this.successCallback, this.errorCallback);
        return (
            <div>
                <h1 className="ui header">My App Component</h1>
                <div>Merry Christmas, React! I really like you!</div>
                <div>The latitude is this: {this.state.latitude}, and the longitude is: {this.state.longitude}</div>
                <SeasonDisplay />
            </div>
        )
    }
}

export default App;
