import React from 'react';

class SeasonDisplay extends React.Component {
    season = this.props.season;

    //text = (this.season === 'summer' ? 'Let&rsquo;s hit the beach' : 'Burr, it&rsquo;s chilly!');

    seasonConfig = {
        summer: { iconName: 'sun icon', text: "Let's hit the beach" },
        winter: { iconName: 'snowflake icon', text: "Burr, it's cold!" }
    }

    iconName = this.seasonConfig[this.season].iconName;

    renderHTML = (rawHTML) => React.createElement("span", { dangerouslySetInnerHTML: { __html: rawHTML } });

    //getIconName = (season) => { return (season === 'summer' ? 'sun icon' : 'snow icon')}

    render(props) {
        return (
            <div>
                <i className={`${this.seasonConfig[this.season].iconName}`} />
                <h1 className="ui header">{this.seasonConfig.season.text}</h1>
                <i className={`${this.seasonConfig[this.season].iconName}`} />
            </div>
        )
    }
}

export default SeasonDisplay;
