class SeasonDisplay extends React.Component {
    render(props) {
        const season = this.props.season;
        return (season == 'summer' ? 'Let&rsquo;s hit the beach'.text() : 'Burr, it&rsquo;s chilly!'.text());
//        return (
//            <div>This should tell us the season. We are in the {this.props.season}</div>
//        )
    }
}

export default SeasonDisplay;
