import React from 'react';

class SeasonDisplay extends React.Component {
    seasonConfig = {
        summer: {},
        winter: {}
    }
    renderHTML = (rawHTML) => React.createElement("span", { dangerouslySetInnerHTML: { __html: rawHTML } });

    getIcon = () => { return (this.props.season === 'summer' ? 'sun' : 'snow')}

    render(props) {
        const season = this.props.season;
        const text = (season === 'summer' ? this.renderHTML('Let&rsquo;s hit the beach') : this.renderHTML('Burr, it&rsquo;s chilly!'));
        return (
            <div>
                <i className={this.getIcon() + ' icon'} />
                <h1 className="ui header">{text}</h1>
                <i className={`${this.getIcon} icon`} />
            </div>
        )
    }
}

export default SeasonDisplay;
