import React from 'react';

class SeasonDisplay extends React.Component {
    renderHTML = (rawHTML) => React.createElement("div", { dangerouslySetInnerHTML: { __html: rawHTML } });

    render(props) {
        const season = this.props.season;
        return (season === 'summer' ? renderHTML('Let&rsquo;s hit the beach') : this.renderHTML('Burr, it&rsquo;s chilly!'));
//        return (
//            <div>This should tell us the season. We are in the {this.props.season}</div>
//        )
    }
}

export default SeasonDisplay;
